        color<span class="token punctuation">[</span>w<span class="token punctuation">]</span> <span class="token operator">=</span> Color<span class="token punctuation">.</span><span class="token constant">GREY</span><span class="token punctuation">;</span>
        queue<span class="token punctuation">.</span><span class="token function">enqueue</span><span class="token punctuation">(</span>w<span class="token punctuation">)</span><span class="token punctuation">;</span>
