<span class="token keyword">const</span> <span class="token function-variable function">depthFirstSearchVisit</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">u<span class="token punctuation">,</span> color<span class="token punctuation">,</span> adjList<span class="token punctuation">,</span> callback</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
